CREATE TABLE Pais (
	Id INT PRIMARY KEY IDENTITY,
	Nombre VARCHAR(255)
);

CREATE TABLE Consumo (
	Año INT,
	PaisId INT FOREIGN KEY REFERENCES Pais(Id),
	Cantidad Decimal(9,3)
	PRIMARY KEY (PaisId, Año)
);

